mapper.select:
select * from t_shop


        and f_type= #{type}


        and f_name like CONCAT("%",#{name},"%")


        and f_shop_type_id = #{shopTypeId}


        and f_agent_id = #{agentId}


order by f_create_time desc
limit #{start},#{limit}

select count(f_id) from t_shop


        and f_type= #{type}


        and f_name like CONCAT("%",#{name},"%")


        and f_shop_type_id = #{shopTypeId}


        and f_agent_id = #{agentId}

select * from t_shop

    f_type = '0' and f_wb=#{wb}

      and f_name like CONCAT("%",#{name},"%")


         and f_agent_id=#{agentId}


order by f_create_time desc

select count(f_id)
from t_shop


        and f_agent_id = #{agentId}


        and f_status = #{status}

select t.f_images as images,t.f_id,t.f_name,t.f_shop_type_id,t.f_type,t.f_icon,t.f_link_name,t.f_link_mobile,t.f_link_address, a.f_name,t.f_remark,t.f_lng,t.f_lat,t.f_code,t.f_per_price,o.comment as commentCount,o.c as saleCount,t.f_score from t_shop t left join t_shop_type a on t.f_shop_type_id = a.f_id left join (select sum(f_sale_count + f_virtual_count) as c,sum(f_comment_count) as comment,f_shop_id from t_product group by f_shop_id) as o on o.f_shop_id=t.f_id
t.f_status = 'NORMAL'
and t.f_wb=#{wb}
and (t.f_province_id=#{region} or t.f_city_id=#{region} or t.f_district_id=#{region})
and a.f_key = #{shopTypeKey}
and (t.f_name like CONCAT("%",#{name},"%") or t.f_id in (select f_shop_id from t_product where f_title like CONCAT("%",#{name},"%") and f_self='1' ))
and (t.f_shop_type_id = #{type} or t.f_shop_type_id in (select f_id from t_shop_type where f_parent_id=#{type}))
and t.f_code = #{code}
order by
o.comment desc,
convert(t.f_per_price,decimal) asc,
o.c desc,
t.f_id asc limit #{start},#{size}

select t.f_images,t.f_id,t.f_name,t.f_shop_type_id,t.f_type,t.f_icon,t.f_link_name,t.f_link_mobile,t.f_link_address, a.f_name,t.f_remark,t.f_lng,t.f_lat,t.f_code,t.f_per_price,t.f_score, round(6378.138*2*asin(sqrt(pow(sin( (t.f_lat*pi()/180-#{lat}*pi()/180)/2),2)+cos(t.f_lat*pi()/180)*cos(#{lat}*pi()/180)* pow(sin( (t.f_lng*pi()/180-#{lng}*pi()/180)/2),2)))*1000) as juli,o.comment as commentCount,o.c as saleCount from t_shop t left join t_shop_type a on t.f_shop_type_id = a.f_id left join (select sum(f_sale_count + f_virtual_count) as c,sum(f_comment_count) as comment,f_shop_id from t_product group by f_shop_id) as o on o.f_shop_id=t.f_id
t.f_status = 'NORMAL'
and t.f_wb=#{wb}
and (t.f_province_id=#{region} or t.f_city_id=#{region} or t.f_district_id=#{region})
and a.f_key = #{shopTypeKey}
and (t.f_district_id = #{code} or t.f_city_id=#{code})
and (t.f_name like CONCAT("%",#{name},"%") or t.f_id in (select f_shop_id from t_product where f_title like CONCAT("%",#{name},"%") and f_self='1' ))
and (t.f_shop_type_id = #{type} or t.f_shop_type_id in (select f_id from t_shop_type where f_parent_id=#{type}))
order by juli asc,t.f_create_time desc limit #{start},#{limit}

select count(t.f_id) from t_shop t,t_shop_type a
t.f_shop_type_id = a.f_id and t.f_status = 'NORMAL'
and t.f_wb=#{wb}
and a.f_key = #{shopTypeKey}
and (t.f_province_id=#{region} or t.f_city_id=#{region} or t.f_district_id=#{region})
and (t.f_district_id = #{districtId} or t.f_city_id=#{districtId})
and (t.f_name like CONCAT("%",#{name},"%") or t.f_id in (select f_shop_id from t_product where f_title like CONCAT("%",#{name},"%") and f_self='1' ))
and (t.f_shop_type_id = #{type} or t.f_shop_type_id in (select f_id from t_shop_type where f_parent_id=#{type}))
and t.f_code = #{code}
